package io.github.scordio.adventofcode2022;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

import java.util.stream.IntStream;

import static org.junit.jupiter.api.Assertions.assertEquals;

@DisplayName("Day 6: Tuning Trouble")
class Day6Test {

  @ParameterizedTest
  @CsvSource({
    "7,    mjqjpqmgbljsphdztnvjfqwrcgsmlb",
    "5,    bvwbjplbgvbhsrlpgdmjqwftvncz",
    "6,    nppdvjthqldpwncqszvftbrmjlhg",
    "10,   nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
    "11,   zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",
    "1794, ",
  })
  void part1(int expected, String input) {
    long answer = IntStream.range(0, input.length() - 4)
      .takeWhile(i -> input.substring(i, i + 4).chars().distinct().count() < 4)
      .count() + 4;

    assertEquals(expected, answer);
  }

  @ParameterizedTest
  @CsvSource({
    "19, mjqjpqmgbljsphdztnvjfqwrcgsmlb",
    "23, bvwbjplbgvbhsrlpgdmjqwftvncz",
    "23, nppdvjthqldpwncqszvftbrmjlhg",
    "29, nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
    "26, zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",
    "2851, ",
  })
  void part2(int expected, String input) {
    long answer = IntStream.range(0, input.length() - 14)
      .takeWhile(i -> input.substring(i, i + 14).chars().distinct().count() < 14)
      .count() + 14;

    assertEquals(expected, answer);
  }

}
